import React, { Component } from 'react';
import './ContactForm.css'
import emailjs from 'emailjs-com';

export default class ContactForm extends Component {
  render() {
    const sendEmail = (e) => {
      e.preventDefault();

      emailjs.sendForm('gmail', 'template_u7hqsni', form.current, 'user_M1bg3x0G8QOErF4c68Woz')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
        e.target.reset()
    };

    return (
      <div className="ContactForm">
        <div className="FormTitle">
          <h1>Contact Me</h1>
        </div>
        <div className="Form">
          {/* <form onSubmit={sendEmail}>
            <div className="row pt-5 mx-auto">
              <div className="col-8 form-group mx-auto">
                <input type="text" className='form-control' placeholder='Subject' name='name' />
              </div>

              <div className="col-8 form-group pt-2 mx-auto">
                <input type="email" className='form-control' placeholder='Email Address' name='email' />
              </div>

              <div className="col-8 form-group pt-2 mx-auto">
                <input type="text" className='form-control' placeholder='Subject' name='subject' />
              </div>

              <div className="col-8 form-group pt-2 mx-auto">
                <textarea type="form-control" className='form-control' id="" cols="30" rows="8" placeholder='Your Message' name='message' ></textarea>
              </div>

              <div className="col-8 pt-3 mx-auto">
                <input type="submit" className='btn- btn-info' value="Send Message"> </input>
              </div>
            </div>
          </form> */}


        <form onSubmit={sendEmail}>

          <div className="form-group">
              <label for="exampleFormControlSelect1">Name</label>
              <input type="text" class="form-control" size="60" placeholder="Name" name='name'/>
            </div>

            <div className="form-group">
              <label for="exampleFormControlSelect1">Subject</label>
              <input type="text" class="form-control" size="60" placeholder="Subject" name='subject'/>
            </div>

            <div className="form-group">
              <label for="exampleFormControlInput1">Email address</label>
              <input type="email" class="form-control" placeholder="Email" name='email'/>
            </div>

            <div className="form-group">
              <label for="exampleFormControlTextarea1">Message</label>
              <textarea className="form-control" rows="3" name='message'></textarea>
            </div>
            <button type="submit">Send Message</button>
        </form>
        </div>
      </div>
    )
  }
}
