      const form = useRef();

      const sendEmail = (e) => {
      e.preventDefault();

      emailjs.sendForm('gmail', 'template_u7hqsni', form.current, 'user_M1bg3x0G8QOErF4c68Woz')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
        e.target.reset()
    };

    return (
      <div className="ContactForm">
        <div className="FormTitle">
          <h1>Contact Me</h1>
        </div>
